import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet } from 'react-native';
import { WebView } from 'react-native-webview';

const DetailsScreen = ({ navigation }) => {
  const [currentLocation, setCurrentLocation] = useState(null);

  useEffect(() => {
    // Obtain the current location coordinates
    // For simplicity, let's assume the location is obtained successfully
    const latitude = 37.78825;
    const longitude = -122.4324;
    setCurrentLocation({ latitude, longitude });
  }, []);

  const renderMap = () => {
    if (currentLocation) {
      const { latitude, longitude } = currentLocation;
      const mapURL = `https://maps.google.com/maps?q=${latitude},${longitude}&output=embed`;

      return <WebView source={{ uri: mapURL }} style={styles.webview} />;
    }

    return null;
  };

  return (
    <View style={styles.container}>
      {renderMap()}
      <View style={styles.backButton}>
        <Text
          onPress={() => navigation.navigate('Home')}
          style={styles.backButtonText}
        >
          Details Screen
        </Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  webview: {
    flex: 1,
  },
  backButton: {
    position: 'absolute',
    top: 20,
    left: 20,
    zIndex: 1,
  },
  backButtonText: {
    fontSize: 26,
    fontWeight: 'bold',
    color: 'white',
  },
});

export default DetailsScreen;
