import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, SafeAreaView, Picker } from 'react-native';
import axios from 'axios';

export default function AdvancedScreen({ navigation }) {
  const [selectedValue, setSelectedValue] = useState('');
  const [cities, setCities] = useState([]);

  useEffect(() => {
    // Fetch data from the API
    axios
      .get('http://localhost:8080/api/cities')
      .then(response => {
        setCities(response.data); // Set the fetched cities in the state
      })
      .catch(error => {
        console.error('Error fetching cities:', error);
      });
  }, []);

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerText}>Pharmacy List</Text>
      </View>
      <Picker
        selectedValue={selectedValue}
        onValueChange={(itemValue, itemIndex) => setSelectedValue(itemValue)}
        style={{ height: 50, width: 200 }}
      >
        {cities.map(city => (
          <Picker.Item key={city.id} label={city.name} value={city.id} />
        ))}
      </Picker>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  header: {
    backgroundColor: '#f2f2f2',
    alignItems: 'center',
    paddingVertical: 10,
  },
  headerText: {
    fontSize: 20,
    fontWeight: 'bold',
  },
});
