import React, { useState } from 'react';
import { View, Text, Picker ,SafeAreaView} from 'react-native';
export default function AdvancedScreen({ navigation }) {
    const [selectedValue, setSelectedValue] = useState('');
  
    return (
        <SafeAreaView style={styles.container}>
             <View style={styles.header}>
        <Text style={styles.headerText}>Pharmacy List</Text>
      </View>
     
        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
          <Text
            onPress={() => navigation.navigate('Home')}
            style={{ fontSize: 26, fontWeight: 'bold' }}>
            Advanced Search Screen
          </Text>
  
          <Picker
            selectedValue={selectedValue}
            onValueChange={(itemValue, itemIndex) => setSelectedValue(itemValue)}
            style={{ height: 50, width: 200 }}
          >
            <Picker.Item label="Option 1" value="option1" />
            <Picker.Item label="Option 2" value="option2" />
            <Picker.Item label="Option 3" value="option3" />
          </Picker>
        </View>
    
      </SafeAreaView>
    );
  }
  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
    },
    header: {
      backgroundColor: '#f2f2f2',
      alignItems: 'center',
      paddingVertical: 10,
    },
    headerText: {
      fontSize: 20,
      fontWeight: 'bold',
    },
    searchBar: {
      height: 40,
      margin: 10,
      borderWidth: 1,
      borderColor: '#ccc',
      paddingHorizontal: 10,
    },
    scrollViewContent: {
      flexGrow: 1,
      alignItems: 'center',
      paddingVertical: 20,
    },
    card: {
      backgroundColor: '#fff',
      borderRadius: 10,
      elevation: 3,
      marginBottom: 20,
      padding: 20,
      alignItems: 'center',
      shadowOffset: {
        width: 0,
        height: 1,
      },
      shadowOpacity: 0.2,
      shadowRadius: 1.41,
    },
    image: {
      width: 100,
      height: 100,
      borderRadius: 50,
      marginBottom: 10,
    },
    cardTitle: {
      fontSize: 18,
      fontWeight: 'bold',
      marginBottom: 5,
    },
    pagination: {
      flexDirection: 'row',
      justifyContent: 'center',
      alignItems: 'center',
      marginTop: 10,
    },
    pageNumber: {
      marginHorizontal: 10,
      fontSize: 16,
      fontWeight: 'bold',
    },
  });
