import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, SafeAreaView, Picker, FlatList } from 'react-native';
import axios from 'axios';

export default function AdvancedScreen({ navigation }) {
  const [selectedCity, setSelectedCity] = useState('');
  const [selectedZone, setSelectedZone] = useState('');
  const [selectedGarde, setSelectedGarde] = useState('');
  const [cities, setCities] = useState([]);
  const [zones, setZones] = useState([]);
  const [pharmacies, setPharmacies] = useState([]);

  useEffect(() => {
    fetchCities();
  }, []);

  useEffect(() => {
    if (selectedCity) {
      fetchZones();
    }
  }, [selectedCity]);

  useEffect(() => {
    if (selectedZone && selectedGarde) {
      fetchPharmacies();
    }
  }, [selectedZone, selectedGarde]);

  const fetchCities = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/cities');
      setCities(response.data);
    } catch (error) {
      console.error('Error fetching cities:', error);
    }
  };

  const fetchZones = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/zones/city/${selectedCity}`);
      setZones(response.data);
    } catch (error) {
      console.error('Error fetching zones:', error);
    }
  };

  const fetchPharmacies = async () => {
    try {
      const response = await axios.get(
        `http://localhost:8080/api/pharmacies/zone/${selectedZone}/garde/${selectedGarde}`
      );
      setPharmacies(response.data);
    } catch (error) {
      console.error('Error fetching pharmacies:', error);
    }
  };

  const handleGardeChange = (itemValue) => {
    setSelectedGarde(itemValue);
  };

  const renderPharmacyCard = ({ item }) => (
    <View style={styles.pharmacyCard}>
      <Text style={styles.pharmacyName}>{item.name}</Text>
      <Text>{item.address}</Text>
      {/* Add more pharmacy details as needed */}
    </View>
  );

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerText}>Pharmacy List</Text>
      </View>
      <View style={styles.pickerContainer}>
        <Text>Type de garde:</Text>
        <Picker
          selectedValue={selectedGarde}
          onValueChange={handleGardeChange}
          style={styles.picker}
        >
          <Picker.Item label="-- Sélectionner le type de garde --" value="" />
          <Picker.Item label="Jour" value="1" />
          <Picker.Item label="Nuit" value="2" />
        </Picker>
      </View>
      <Picker
        selectedValue={selectedCity}
        onValueChange={(itemValue, itemIndex) => setSelectedCity(itemValue)}
        style={styles.picker}
      >
        {cities.map((city) => (
          <Picker.Item key={city.id} label={city.name} value={city.id} />
        ))}
      </Picker>
      {selectedCity && (
        <Picker
          selectedValue={selectedZone}
          onValueChange={(itemValue, itemIndex) => setSelectedZone(itemValue)}
          style={styles.picker}
        >
          {zones.map((zone) => (
            <Picker.Item key={zone.id} label={zone.name} value={zone.id} />
          ))}
        </Picker>
      )}
      {selectedZone && selectedGarde && (
        <FlatList
          data={pharmacies}
          keyExtractor={(item) => item.id.toString()}
          renderItem={renderPharmacyCard}
        />
      )}
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  header: {
    backgroundColor: '#f2f2f2',
    alignItems: 'center',
    paddingVertical: 10,
  },
  headerText: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  pickerContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginVertical: 10,
    paddingHorizontal: 20,
  },
  picker: {
    flex: 1,
    height: 50,
    width: '100%',
  },
  pharmacyCard: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    padding: 10,
    marginVertical: 5,
  },
  pharmacyName: {
    fontWeight: 'bold',
    marginBottom: 5,
  },
});
